definition.script:
//This list is used to save nodes which were already alocated by one of the tests so that tests will be spreaded to diffrent nodes 
allocated_nodes=[]

pipeline {
agent { node { label 'master' } }
parameters {
        string(description: 'What is the Archer build version you want to test? \n example: 17.12.00.20600', name: 'BUILD_VERSION')
        choice(choices: 'Tasks\nFormal\nScheduled', description: 'What is your build type?', name: 'BUILDTYPE')
        text(description: 'What tests needs to run and in which confgurations? \n example: [[SuiteName,SuitePath,ToolType,ATEControllerMachineLabel,SimulatorEngineMachineLabel,BaseImagePath,BaseImageName,ATEScriptsPath]]', name: 'TestsArray')
        booleanParam(name: 'RunNunitTests', defaultValue: false, description: 'Should Nunit intergration tests be executed?')
		booleanParam(name: 'PublishToTestRail', defaultValue: false, description: 'Should sanity results be published to TestRail system?')

    }

    stages {
        stage ("Run ATE Tests"){

            steps{
                script{
                    //Delete new line characters and read all test arrays 
                    def stringMap= params.TestsArray.replaceAll("[\n\r]", "").replaceAll('^\\[|\\]$','')
                    //send all test arrays to processing
                    runparalleltests(stringMap)
                }

            }
        }
    }

}
// Run multiple ATE tests
def runparalleltests(AUT_Tests){
//Define 'Auto Deploy Sanity' tests exection list
def ate_tests = [:]
//For symantic
def index=0
def masternode="master"
def buildversion= params.BUILD_VERSION
def ate_MajorMinor_Ver=""
if(params.BUILD_VERSION!="") ate_MajorMinor_Ver =buildversion.substring(0,5)
//Read each test details and creation a job trigger to AutoDeploy Sanity flow with with the given details
AUT_Tests.tokenize(';').each{    
                    testDetails = it.replaceAll('^\\[|\\]$','').tokenize(',')
                    def test_suite_name = testDetails[0]
					def test_suite_path = testDetails[1]
					def tool_type = testDetails[2]
					def ate_controller_engine_label = testDetails[3]
					def simulator_engine_label = testDetails[4]
					def base_image_path = testDetails[5]
					def base_image = testDetails[6]
					def ate_scripts_path = testDetails[7]

  ate_tests["${index}. Test ${test_suite_name} for ${tool_type}"] = {
    build job:"RunTestSuite",parameters: [
      booleanParam(name: 'RunNunitTests', value:params.RunNunitTests),
	  booleanParam(name: 'PublishToTestRail', value:params.PublishToTestRail),
      booleanParam(name: 'RestoreAndReinstall', value:true),
      string(name: 'BUILD_VERSION', value:params.BUILD_VERSION),
	  string(name: 'BaseImage', value:base_image),
	  string(name:'ToolType',value:tool_type),
	  string(name:'BUILDTYPE',value:params.BUILDTYPE),
	  string(name: 'BaseImagePath', value:base_image_path),
	  string(name: 'TestSuite', value:test_suite_name),
	  string(name: 'MajorMinor_Ver', value:ate_MajorMinor_Ver),
	  [$class: 'NodeParameterValue', name: 'FlowController', labels: ["${masternode}"], nodeEligibility: [$class: 'AllNodeEligibility']],
	  [$class: 'NodeParameterValue', name: 'ATEControllerEngine', labels: ["${ate_controller_engine_label}"], nodeEligibility: [$class: 'AllNodeEligibility']],
      [$class: 'NodeParameterValue', name: 'SimulatorEngine', labels: ["${simulator_engine_label}"], nodeEligibility: [$class: 'AllNodeEligibility']]]

  }
  index++
}
  //Executing all tests in parallel
parallel ate_tests

}

