definition.script:
pipeline {
    agent none
    stages {
        stage('checkout RTC') {
            parallel {
                stage('Debug code load') {
                    agent {
                        label "SCM-Builds-01"
                    }
                    steps {
                        teamconcert ([
							 buildType: [
										value:'buildWorkspace', 
										buildWorkspace: params.WSName, 
										acceptBeforeLoad:params.AcceptBeforeLoad,
										clearLoadDirectory:true, 
										createFoldersForComponents:true, 
										customizedSnapshotName:params.SnapshotName, 
										loadDirectory:params.team_scm_fetchDestination,
										changelog:true
										]
									])
                    }
                    post {
                        always {
                            junit "**/TEST-*.xml"
                        }
                    }
                }
                stage('Release code load') {
                    agent {
                        label "SCM-Builds-02"
                    }
                    steps {
                        teamconcert ([
							 buildType: [
										value:'buildWorkspace', 
										buildWorkspace: params.WSName, 
										acceptBeforeLoad:params.AcceptBeforeLoad,
										clearLoadDirectory:true, 
										createFoldersForComponents:true, 
										customizedSnapshotName:params.SnapshotName, 
										loadDirectory:params.team_scm_fetchDestination,
										changelog:true
										]
									])
                    }
                    post {
                        always {
                            junit "**/TEST-*.xml"
                        }
                    }
                }
            }
        }
    }
}
