definition.script:
pipeline {
    agent { node { label 'master' } }
    parameters {
        string(description: 'What is the name of the machine to clone? \n example: SCM-SIM-V30', name: 'BaseMachineName')
        string(description: 'Please suggest a name to the new machine? \n example: SCM-SIM-V90',  name: 'TargetMachineName')
        choice(choices: 'linked\nfull', description: 'Which clone type you would like to use? \n linked: A linked clone is a copy of a virtual machine that shares virtual disks with the parent virtual machine in an ongoing manner. Ongoing changes to the virtual disk of the parent do not affect the linked clone, and changes to the disk of the linked clone do not affect the parent. \n full: A full clone is a complete and independent copy of a virtual machine. It shares nothing with the parent virtual machine after the cloning operation. Ongoing operation of a full clone is entirely separate from the parent virtual machine.', name: 'CloneType')
        string(description: 'What is the source machine snapshot name? \n In case of empty value, machine will be created from last source machine start \n example: SCM-SIM-V90', default:'LATEST', name: 'SnapshotName')
        string(description: 'Would you like to set a special MAC Adress to the target machine? if yes please specify, if not new machine will get the MAC address of the source one\nMAC Address pattern: 00:50:56:xx:xx:xx\n', default:'LATEST', name: 'MacAddress')

    }
    environment {
        VMWareHomeDir='C:\Program Files (x86)\VMware\VMware Workstation'
        VMWareExe='vmrun.exe'
        VMMachinesRootPath='E:\Virtual Machines'
    }
    stages {
        stage ("Verify and shudown source VM"){

            steps{
                script{
                    shutdownMachine(params.BaseMachineName)
                }
            }
        }
        stage ("Clonning VM"){
            steps{
                script{
                    cloneMachine(params.BaseMachineName,params.TargetMachineName)
                }
            }
        }
        stage ("Start clonned VM"){
            steps{
                script{
                    startMachine(params.TargetMachineName)
                    sleep time:2 unit:MINUTES
                }
            }
        }
        stage ("Set Machine Name and configure MACAddress"){
            steps{
                script{
                    updateMachineName(params.TargetMachineName)
                    updateMachineMAC(params.TargetMachineName,params.MACAddress)
                }
            }
        }
        stage ("Start new VM and connect it to Jenkins"){
            steps{
                script{
                    startMachine(params.TargetMachineName)
                }
            }
        }
    }
}


def updateMachineName(String machine){
    f=new File(VMMachinesRootPath+'\\'+machine+'\\COMPUTERNAME.txt')
    file << machine
    bat '${VMWareHomeDir}\\${VMWareExe} -T ws -gu autologonuser -gp Columbia03 copyFileFromHostToGuest ${VMMachinesRootPath}\\${machine}\\${machine}.vmx ${VMMachinesRootPath}\\${machine}\\COMPUTERNAME.txt C:\\COMPUTERNAME.txt'
}

def shutdownMachine(String machine){
   bat '${VMWareHomeDir}\\${VMWareExe} -T ws stop ${VMMachinesRootPath}\\${machine}\\${machine}.vmx soft'
}

def startMachine(String machine){
   bat '${VMWareHomeDir}\\${VMWareExe} -T ws start ${VMMachinesRootPath}\\${machine}\\${machine}.vmx nogui'
}

def cloneMachine(String machine,String newMachine){
   bat '${VMWareHomeDir}\\${VMWareExe} -T ws clone ${VMMachinesRootPath}\\${machine}\\${machine}.vmx ${VMMachinesRootPath}\\${newMachine}\\${newMachine}.vmx ${params.CloneType} ${params.SnapshotName} -cloneName=${newMachine}'
}

def updateMachineMAC(String machine,String MACAddress){
    def file = new File('${VMMachinesRootPath}\\${machine}\\${machine}.vmx ${VMMachinesRootPath}\\${newMachine}\\${newMachine}.vmx')
    def newConfig = file.text.replaceRegex('ethernet0.addressType\=.*', 'ethernet0.addressType=static')
    file.text = newConfig.replaceRegex('ethernet0.address\=.*', 'ethernet0.address=${MACAddress}')
}
