def file = new File(emailGroupsDefFile)
def reciepts_list="";
  	if (file.exists()){
        //println "Below is the content of the file ${file.absolutePath}"
      def lineNo = 1
      def line
      file.withReader { reader ->
         while ((line = reader.readLine())!=null) {
            if (line.trim() =="") continue;
            //println "${lineNo}. ${line}"
            lineNo++
            groupOwn=line.substring(0,line.indexOf("="))
            reciepts_list+=recipientList.contains(groupOwn) ? line.substring(line.indexOf("=")+1).trim():"";

            reciepts_list=(!reciepts_list.trim().endsWith(",") & !reciepts_list.trim().equals ("")) ?reciepts_list.trim()+",":reciepts_list.trim()

         }
      }

    }
      	def map = [ "recipients_list":reciepts_list]
	return 	map
