definition.script:
pipeline {
    agent { node { label 'master' } }
     parameters {
         string (name: 'WorkItem', defaultValue:'', description: 'What is the Work Item number of the new project?')
         text (name: 'streamTitle', defaultValue:'', description:'What is the short project title (use _ as delimter for multiple words, max 40 characters)?')
        //string(name: 'SuggestedStreamName', defaultValue: '', description: 'What should the new stream be named with? Prefered syntax:Archer_17.12_TaskName_WorkItemNum')
        string(name: 'SourceStreamName', defaultValue: '', description: 'What is your base stream name?')
        text(name: 'SourceStreamSnapshotName', defaultValue: '', description: "What is the stream snapshot name you would use as a base for the new stream? Examples:UOMD_BLD_17.12.00.30100 UOMD_BLD_17.12.00.20600")
    }
    environment {
        snapshot_name_prefix="UOMD_BLD_"
        search_snaposhots_cmd="scm list snapshots -r http://clm.kla-tencor.com/ccm -u omdbuild -P Scm07^& -n ${params.SourceStreamSnapshotName}"
        search_snaposhots_by_stream_cmd="scm list snapshots -r http://clm.kla-tencor.com/ccm -u omdbuild -P Scm07^& -m 1 ${params.SourceStreamName}"
        search_source_stream_cmd="scm list stream -r http://clm.kla-tencor.com/ccm -u omdbuild -P Scm07^& -n ${params.SourceStreamName}"
        search_target_stream_cmd="scm list stream -r http://clm.kla-tencor.com/ccm -u omdbuild -P Scm07^& -n ${suggested_stream_name}"
    }
        stages {
    			stage ('verification') {
    				steps {
        				    script {
        				        suggested_stream_name="${params.SourceStreamName}".substring(0,"${params.SourceStreamName}".lastIndexOf("_")-1)+"_${params.streamTitle}_${params.WorkItem}"
                                wrap([$class: 'BuildUser']) {
            				        currentBuild.description = "Request for new stream creation '${suggested_stream_name}' by '${env.BUILD_USER}'"
                                }
                                print ("Index of space:" +suggested_stream_name +" . . . . " + suggested_stream_name.indexOf(" "))
                                switch(suggested_stream_name.indexOf(" ")){
                                    case -1 :
    							       print 'New stream doesnt conatin spaces'
    							       break;
                                    default:
                                        msg="Error: stream name: '${suggested_stream_name}' is invalid. Stream name can't contain white characters [spaces]"
                                        input(message:msg)
                                        throw new Exception(msg)

                                }
                                switch(suggested_stream_name.length()){
    						        case 71..Integer.MAX_VALUE :
    						           msg="Error: suggested stream name: '${suggested_stream_name}' is longer than 70 characters"
    						           input(message:msg ,ok:'Abort')
    							       throw new Exception(msg)
                                    default:
                                        print 'new stream legth was verified'
						        }

    	    					print 'verify suggested stream name is not occupied'
    						    switch(searchStrInCommandOutput(search_target_stream_cmd,suggested_stream_name) >= 1){
    						        case 1..Integer.MAX_VALUE :
    						           msg="Stream name " + suggested_stream_name + " is already occupied"
    						           input(message:msg ,ok:'Abort')
    							       throw new Exception(msg)
                                    default:
                                        print 'suggested stream name is free'
    						    }

    	    					print 'verify source stream name'
    	    					switch(searchStrInCommandOutput(search_source_stream_cmd,params.SourceStreamName)){
    	    					    case 2..Integer.MAX_VALUE :
    	    					            msg='Multi stream with prefix ' + params.SourceStreamName
    						                input(message:msg ,ok:'Abort')
    							            throw new Exception(msg)
    	    					    case Integer.MIN_VALUE+2..0 :
    	    					            msg='Invalid source stream name '  + params.SourceStreamName
    	    					            input(message:msg ,ok:'Abort')
    							            throw new Exception(msg)
    	                            default:
    	                                print 'source stream name is verified'
    	    					}

    	    					print 'verify source stream snapshot'
    	        				switch(searchStrInCommandOutput(search_snaposhots_cmd,params.SourceStreamSnapshotName)){
    	        				    case 2..Integer.MAX_VALUE :
    	        				            msg="Multi snapshots with prefix "+params.SourceStreamSnapshotName
    						                input(message:msg ,ok:'Abort')
    							            throw new Exception(msg)
    	                            case Integer.MIN_VALUE+2..0 :
    	                                    msg='Invalid snapshot name: '+params.SourceStreamSnapshotName+' for stream: ' +params.SourceStreamName
    						                input(message:msg ,ok:'Abort')
    							            throw new Exception(msg)
                                    default:    
    					                    print 'snapshot name is verified'
                                }   
        				    }
    				}
    			}
    			stage ('waiting_CM_approval') {
    			 steps {
    			     script {
    			         //def userInput
    			         try{
    			             wrap([$class: 'BuildUser']) {
        			             emailext (to: "DL-OMD-ENG-SCM@KLA-tencor.com",
                                     subject: "New stream request is pending for your approval",
                                     body: """<p>Dear CM team </p><p>New stream request requested by ${env.BUILD_USER} with name  '${suggested_stream_name}' is pending for your approval<p>
                                     <p>To approve the request, please visit ${env.BUILD_URL}input/ <p>""");
    			             }
            			     env.userInput = input(
            				 id: 'userInput', message: 'CM team\nPlease approve the below stream name?', parameters: [
            				 [$class: 'TextParameterDefinition', defaultValue: suggested_stream_name, description: 'Suggested new stream name', name: 'userInput']
            				],submitter: 'DL-OMD-ENG-SCM',ok:'Approved, lets create it')
            			//	print (userInput)
    			         } catch (err) { // input false
    			            print("ERROR:" + err)
                            env.userInput = input(
            				 id: 'userInput', message: 'Please update abortion reason?', parameters: [
            				[$class: 'TextParameterDefinition', defaultValue: '', description: 'Decline reason', name: 'declineReason']]
            				,submitter: 'DL-OMD-ENG-SCM',ok: 'Email user, close request')
                            def user = err.getCauses()[0].getUser()
                            echo "Aborted by: [${user}]"
                            echo "Abort reason: "+ env.userInput
                            //throw new Exception( "Abort reason: "+ userInput)
                            wrap([$class: 'BuildUser']) {
                                emailext (
                                    subject: "Declined: Stream creation request ${suggested_stream_name}'",
                                    body: """<p>Dear ${env.BUILD_USER} </p>
                                    <p>Your request for new sream with the name <b>${suggested_stream_name}</b> was <strong>declined<strong> by <b>${user}</b></p>
                                    <p>Reason: ${env.userInput}</p>
                                    <p>For execution details, please check console output at "<a href="${env.BUILD_URL}console">${env.JOB_NAME} [R${env.BUILD_NUMBER}]</a>"</p>""",
                                     to: "${BUILD_USER_EMAIL}"
                                )
                            }
                        }
                        print (env.userInput)
    				if (!suggested_stream_name.equals(env.userInput)){ 
    				        print 'verify new suggested stream name is not occupied '+ env.userInput
						    switch(searchStrInCommandOutput(search_target_stream_cmd,suggested_stream_name) >= 1){
						        case 1..Integer.MAX_VALUE :
                                    msg="Stream name " + env.userInput + " is already occupied"
    						        input(message:msg ,ok:'Abort')
    							    throw new Exception(msg)
                                default:
                                    print 'new suggested stream name is free'
						    }
    			     }
    			 }
    			}
    			}
    			stage ('creation') {
                    steps {
                        script {
                             wrap([$class: 'BuildUser']) {
                                create_stream_cmd="scm create stream -r http://clm.kla-tencor.com/ccm -u omdbuild -P Scm07^&  ${env.userInput}  --description \"Created by ${BUILD_USER}, based on stream ${params.SourceStreamName}, snapshot ${params.SourceStreamSnapshotName} and work item $params.WorkItem}\" --snapshot ${params.SourceStreamSnapshotName}  --projectarea \"OMD NuArcher\" "
                                switch(searchStrInCommandOutput(create_stream_cmd," successfully created.")){
    						        case 0:
    						            msg="Stream creation faild"
    						            input(message:msg ,ok:'Abort')
    							        throw new Exception(msg)
                                    default:
                                        print 'New stream was successfully created'
    						    }

								create_stream_snapshot_cmd="scm create snapshot -r http://clm.kla-tencor.com/ccm -u omdbuild -P Scm07^&  -f ${env.userInput}  -d \"Init snapshot create automatically by ${BUILD_USER}, based on stream ${params.SourceStreamName}, snapshot ${params.SourceStreamSnapshotName} and work item $params.WorkItem}\" -n ${params.SourceStreamSnapshotName}"
								searchStrInCommandOutput(create_stream_snapshot_cmd," successfully created.")
                             }
                        }
                    }
    			}
        }
    	post {
            failure {
                wrap([$class: 'BuildUser']) {
                    // notify users when the Pipeline fails
                    emailext (to: "${BUILD_USER_EMAIL}",
                    subject: "Stream ${env.userInput} creation - FAILED",
                    body: """Something is wrong during stream creation, please check creation log via ${env.BUILD_URL}""");
                }
            }

            success {
                wrap([$class: 'BuildUser']) {
                    // notify users when the Pipeline success
                    emailext (to: "${BUILD_USER_EMAIL}",
                    subject: "Stream ${env.userInput} creation - SUCCESS",
                    body: """Dear ${env.BUILD_USER} <BR> Your request for new sream with the name ${env.userInput} was approved. <BR> New stream was created based on ${params.SourceStreamName} and snapshot ${params.SourceStreamSnapshotName}.<BR> Regards <BR> OMD DevOps team""");
                }
            }
        }

}

def searchStrInCommandOutput(cmd,str){
    stdoutput = bat(returnStdout:true , script:cmd).trim()
    //print ("stdout=" + stdout)
    result = stdoutput.readLines().drop(1).join(" ")
    //print ("count=" +result.count(str))
    return result.count(str)
}
