javaposse.jobdsl.plugin.ExecuteDslScript.scriptText:
job("OMD_${Version}_Scheduled_Trigger") {
    logRotator(30, -1)
  	parameters {
        stringParam('MajorMinor_Ver', '17.14','The majorMinor Version of the build')
      	stringParam('emailGroups', 'DL-OMD-SW-SQC@kla-tencor.com,DL-OMD-SW-SCOL@KLA-Tencor.com,DL-OMD-SW-Imaging@KLA-Tencor.com,DL-OMD-ENG-SCM@KLA-Tencor.com,DL-OMD-SW-ARCH@KLA-Tencor.com', 'Recipients of the Scheduled build')
      	nodeParam('ReleaseBuildEngine') {
            description('select the release build machine')
            defaultNodes(['SCM-Builds-V01'])
            allowedNodes(['SCM-Builds-V01', 'SCM-Builds-V02', 'SCM-Builds-V03','SCM-Builds-V04'])
        }
      	nodeParam('DebugBuildEngine') {
            description('select the debug build machine')
            defaultNodes(['SCM-Builds-V02'])
            allowedNodes(['SCM-Builds-V01', 'SCM-Builds-V02', 'SCM-Builds-V03','SCM-Builds-V04'])
        }
      	booleanParam('SkipSandboxLoad', false, '')
      	stringParam('buildResultUUID', '', 'The UUID of the build result in RTC. It is supplied by builds initiated through RTC. For builds initiated through Hudson/Jenkins, no value should be supplied.')
    	choiceParam('StaticCodeAnalysisModeDebug', ['ErrorOnBuildEnd', 'Disabled', 'Warning','Error'], 'SCA mode for debug build')
      	choiceParam('StaticCodeAnalysisModeRelease', ['ErrorOnBuildEnd', 'Disabled', 'Warning','Error'], 'SCA mode for release build')
    	booleanParam('Auto_Deploy_Sanity', true, 'will the auto deploy and sanity run for this build?')
    	textParam('TestsArray', 
                  '''[[Autorun_XYS_Suite_ATE_A700,X:\\ATE\\TestCases\\SQC\\Imaging\\XYS\\Autorun,Archer700,SCM-ATE-01,SimulatorEngine,D:\\BaseImage,17.11.00.21200_A700_Nunit_Image,\\\\isr1vmappcm01\\Automated_Testing\\17.10_ATE];
					[AROL_Autorun_ATE_Suite,X:\\ATE\\TestCases\\SQC\\MBS\\AROL,Archer TL,SCM-ATE-01,SimulatorEngine,D:\\BaseImage,17.03.00.30400_TL_Mil_Nunit_Image,\\\\isr1vmappcm01\\Automated_Testing\\17.0x_ATE];
					[Autorun_XYS_Suite_ATE_A600,X:\\ATE\\TestCases\\SQC\\Imaging,Archer600,SCM-ATE-01,SimulatorEngine,D:\\BaseImage,17.02.01.30100_A600_Mil_Nunit_Image,\\\\isr1vmappcm01\\Automated_Testing\\17.0x_ATE]]''', 
                  'What tests needs to run and in which confgurations?')
    }
  	environmentVariables {
        keepSystemVariables(true)
    	keepBuildVariables(true)
    	overrideBuildParameters()
    	envs(BUILDTYPE: 'Scheduled', WSName: 'omdbuild_Archer_${Verseion}_Dev_Workspace',SnapshotName:'INIT_UOMD_BLD_${BUILD_VERSION}')

    }
  	scm {
        rtc {
          buildDefinition('OMD-Archer-V${Version}-Scheduled-Jenkins')
        }
    }
  	triggers {
        scm('30 00 * * 0-5')
    }
  	wrappers {
        timestamps()
      	preBuildCleanup()
        environmentVariables {
        groovy('''
            def action = currentBuild.getAction(com.ibm.team.build.internal.hjplugin.RTCBuildResultAction.class)
			def buildProps = action.getBuildProperties()
			def bldResultUUID=buildProps['RTCBuildResultUUID']
			def teamscmfetchDestination=buildProps['team_scm_fetchDestination']
			def teamscmworkspaceUUID=buildProps['team_scm_workspaceUUID']
			print ("buildResultUUID="+ bldResultUUID)
			def map = [buildResultUUID: bldResultUUID,team_scm_workspaceUUID:teamscmworkspaceUUID,team_scm_fetchDestination:teamscmfetchDestination] 
			return map 
        ''')
        }
         preScmSteps {
            steps {
                 environmentVariables {
                    envs(VERSIONFILE: '${BUILD_VERSION}', AUTOVERSION: 'false')
                 }
                 conditionalSteps {
            		condition {
                		fileExists('${BUILD_VERSION}', BaseDir.WORKSPACE)
            		}
            		runner('Fail')
            		steps {
                		batchFile(''':: run command for incrementing the version
						set VERSIONCONFIG=\\\\isr1appcc02\\SCM\\OMD\\Jenkins_Build_Version\\VersionConfig.properties
                        set team_scm_fetchDestination=\\\\isr1appcc02\\SCM\\OMD\\Jenkins_Build_Version
                        set BUILDVERSION=%BUILD_VERSION%
                        CALL \\\\isr1appcc02\\SCM\\OMD\\Jenkins_Build_Version\\OMD_Builds\\JenkinsBuild\\VersionAutoIncremental\\AutoVersion.bat
                        IF %ERRORLEVEL% NEQ 0 (
                               ECHO "Unable to calculate BUILDVERSION"
                            Exit /b 1
						)''')
                      	environmentVariables {
                            envs(VERSIONFILE: '${BUILD_VERSION}', AUTOVERSION: 'true',BUILD_VERSION:'${CurrentRunnningVersion}',SnapshotName:'INIT_UOMD_BLD_${CurrentRunnningVersion}')
                            propertiesFile('${BUILD_VERSION}')
       					}
            		}
        		}
              	batchFile('C:\\PROGRA~1\\Unlocker\\Unlocker.exe %WORKSPACE% /S')
        	}

        }
    }
  	steps {
        batchFile('C:\\Progra~2\\Jenkins\\tools\\hudson.tasks.Ant_AntInstallation\\RenameBuildLabel\\bin\\ant.bat -file %team_scm_fetchDestination%\\OMD_Builds\\JenkinsBuild\\VersionUpdater\\Build.xml -DbuildResultUUID=%buildResultUUID% -Dlabel="%BUILDTYPE%_UOMD_BLD_%BUILD_VERSION%" "-Dpassword=Scm07&" -DuserId=omdbuild -DtoolkitPath=C:\\Build\\TeamConcertBuild\\buildsystem\\buildtoolkit -DsnapshotUUID=%team_scm_workspaceUUID% "-DrepositoryAddress=%repositoryAddress%" -lib C:\\Build\\TeamConcertBuild\\buildsystem\\buildtoolkit')
        downstreamParameterized {
			trigger('RDM_NuArcher_CI_Flow') {
				  parameters {
						currentBuild()
						predefinedProp('buildRequesterUserId','omdbuild')
						predefinedProp('BUILD_VERSION','${BUILD_VERSION}')
						predefinedProp('ReleaseBuildEngine','${ReleaseBuildEngine}')
						predefinedProp('DebugBuildEngine','${DebugBuildEngine}')
						predefinedProp('MajorMinor_Ver','${MajorMinor_Ver}')
						predefinedProp('WSName','${WSName}')
						predefinedProp('team_scm_fetchDestination','D:\\Sandboxes\\omdbuild_${BUILD_VERSION}')
						predefinedProp('buildRequesterUserId','${buildRequesterUserId}')
						predefinedProp('ClosureBuildEngine','master')
						predefinedProp('BUILDTYPE','${BUILDTYPE}')
						predefinedProp('VERSIONFILE','${VERSIONFILE}')
						predefinedProp('AUTOVERSION','${AUTOVERSION}')
						predefinedProp('RunningMode','DAILY')
						predefinedProp('BuildAutoApproved','true')
						predefinedProp('SnapshotName','${SnapshotName}')
						predefinedProp('buildResultUUID','${buildResultUUID}')
						predefinedProp('team_scm_workspaceUUID','${team_scm_workspaceUUID}')
						predefinedProp('CheckSimTimeout','180')
						predefinedProp('RunNunitTests','true')
				  }
			 }
		}
    }
  	publishers {
        mailer('DL-OMD-ENG-SCM@kla-tencor.com', true, false)
    }
}
listView('Testing') {
    jobs {
        name('OMD_${Version}_Scheduled_Trigger')
    }
}
