hudson.plugins.emailext.ExtendedEmailPub.presendScript:
def buildtype = ((build.getEnvVars().get("BUILDTYPE")!=null)&&(build.getEnvVars().get("BUILDTYPE")!=""))?build.getEnvVars().get("BUILDTYPE")+" - ":"";
def additional =((build.getEnvVars().get("NIGHTLY") != null) &&(build.getEnvVars().get("NIGHTLY").equals("TRUE"))) ? "Nightly - ":"";
def msg_version = build.getEnvVars().get("BUILD_VERSION");

if(buildtype=="Formal" && build.result.toString()=="SUCCESS")
msg.setSubject("OMD-UN "+ msg_version +" Integ Build " );
else msg.setSubject(buildtype + additional + "OMD-Archer-Build - Build # "+ msg_version +" - " + build.result.toString());



//Final update for build description
build.setDescription(buildtype + additional  + build.getEnvVars().get("BUILD_VERSION") + " triggered by '" + User.current().getId());

hudson.plugins.emailext.ExtendedEmailPub.postsendScript:
import org.jvnet.hudson.plugins.groovypostbuild.*;
import  javax.mail.Multipart;

// create the summary with the document icon and attach to the build
GroovyPostbuildSummaryAction action = new GroovyPostbuildSummaryAction("notepad.png");
build.getActions().add(action);
// Read mail content and attach it to the build summary
Multipart mp= (Multipart)msg.getContent()
action.appendText(mp.getBodyPart(0).getContent().toString(), false);
//save the build!
build.save()
