definition.script:
pipeline {
     agent {label 'master'}
     environment { 
        repositoryAddress = 'http://clm.kla-tencor.com/ccm'
        networkpath='\\\\klasj\\ktfiles\\Regions\\Israel\\SWGRP\\SW-OMD'
    }
    parameters {
        string(name: 'Version', description: 'Which Version you want to Create? eg:17.14 or 17.14.01')
		string(name: 'SourceStreamName', defaultValue: '', description: 'What is your Source stream name?')
		string(name: 'SourceStreamSnapshotName', defaultValue: '', description: 'What is your Source stream name snapshot name?')
    }

	stages {    
	    stage ('Validation') {
            steps ('vaiidate') {
	                script{
	                if(params.Version==""){
    				    exitwitherror("Version is not defined")
	                }  
					MajorMinorSP=params.Version
					if(params.Version.length()<6) MajorMinorSP=params.Version+".00"

					def userInput=input(
					id: 'userInput', message: 'Please verify the below parameters:', parameters: [
					string(defaultValue: "Archer_${params.Version}_Dev",description: 'Dev stream name',name: 'DevStream'),
					string(defaultValue: "Archer_${params.Version}_Integ",description: 'Integ stream name',name: 'IntegStream'),
					string(defaultValue: "omdbuild_Archer_${params.Version}_Dev_Workspace",description: 'Dev workspace name',name: 'DevWorkspace'),
					string(defaultValue: "omdbuild_Archer_${params.Version}_Integ_Workspace",description: 'Integ workspace name',name: 'IntegWorkspace'),
					string(defaultValue: "OMD-Archer-V${params.Version}-Scheduled-Jenkins",description: 'Scheduled build definition name',name: 'ScheduledBD'),
					string(defaultValue: "OMD-Archer-V${params.Version}-Formal-Jenkins",description: 'Formal build definition name',name: 'FormalBD'),
					string(defaultValue: "OMD_{params.Version}_Scheduled_Trigger",description: 'Scheduled build job trigger',name: 'ScheduledTrigger'),
					string(defaultValue: "OMD_{params.Version}_Formal_Trigger",description: 'Formal build job trigger',name: 'FormalTrigger'),
					string(defaultValue: "${params.Version}version.txt",description: 'Version file name',name: 'Versionfile'),
					string(defaultValue: "${MajorMinorSP}.20000",description: 'The starting build numbering',name: 'StartingVersion'),
					],submitter: 'DL-OMD-ENG-SCM',ok:'Corret, lets create it')


					env.InputDevStream = userInput.DevStream
					env.InputIntegStream = userInput.IntegStream
					env.InputDevWorkspace = userInput.DevWorkspace
					env.InputIntegWorkspace = userInput.IntegWorkspace
					env.InputScheduledBD = userInput.ScheduledBD
					env.InputFormalBD = userInput.FormalBD
					env.InputScheduledTrigger = userInput.ScheduledTrigger
					env.InputFormalTrigger = userInput.FormalTrigger
					env.InputVersionfile = userInput.Versionfile
					env.InputStartingVersion = userInput.StartingVersion
	            }
            }
	    }
		stage ('CreateStream'){
			steps ('CreateStream'){
				build job: 'CreateStreamCM', parameters: 
				[
				[$class: 'TextParameterValue', name: 'streamTitle', value:env.InputDevStream],
				[$class: 'StringParameterValue', name: 'SourceStreamName', value: params.SourceStreamName],
				[$class: 'TextParameterValue', name: 'SourceStreamSnapshotName', value: params.SourceStreamSnapshotName]
				], wait: true
				build job: 'CreateStreamCM', parameters: 
				[
				[$class: 'TextParameterValue', name: 'streamTitle', value:env.InputIntegStream],
				[$class: 'StringParameterValue', name: 'SourceStreamName', value: params.SourceStreamName],
				[$class: 'TextParameterValue', name: 'SourceStreamSnapshotName', value: params.SourceStreamSnapshotName]
				], wait: true
			}

		}
		stage ('CreateWorkspace'){
			steps ('CreateWorkspace') {
	                script{
						bat "scm create workspace -r ${env.repositoryAddress} -u ${env.builduser} -P ${env.scmbuildpass} -s ${env.InputDevStream} ${env.InputDevWorkspace}"
						bat "scm create workspace -r ${env.repositoryAddress} -u ${env.builduser} -P ${env.scmbuildpass} -s ${env.InputIntegStream}  ${env.InputIntegWorkspace}"
					}
			}
		}
		stage ('CreateBuildDefinition'){
			steps ('CreateBD'){
				build job: 'CreateBuildDefinition', parameters: 
				[
				[$class: 'StringParameterValue', name: 'RTCWorkspace', value:env.InputDevWorkspace],
				[$class: 'StringParameterValue', name: 'BuildDefinationName', value: env.InputScheduledBD],
				[$class: 'StringParameterValue', name: 'BUILDTYPE', value: "Scheduled"]
				], wait: true
				build job: 'CreateBuildDefinition', parameters: 
				[
				[$class: 'StringParameterValue', name: 'RTCWorkspace', value:env.InputIntegWorkspace],
				[$class: 'StringParameterValue', name: 'BuildDefinationName', value: env.InputFormalBD],
				[$class: 'StringParameterValue', name: 'BUILDTYPE', value: "Formal"]
				], wait: true
			}
		}
		stage ('CreateJenkinstrigger'){
			steps ('CreateTriggerJob'){
				build job: 'CreateScheduledTrigger', parameters: 
				[
				[$class: 'StringParameterValue', name: 'Version', value:params.Version]
				], wait: true
				build job: 'CreateFormalTrigger', parameters: 
				[
				[$class: 'StringParameterValue', name: 'Version', value:params.Version],
				], wait: true
			}
		}
		stage ('CreateVersionFile'){
			steps{
				script{
					File VersionFile = new File(env.InputVersionfile)
					if(VersionFile.exists()) VersionFile.delete()
					VersionFile.write "CurrentVersion="+env.InputStartingVersion+"\nCurrentRunnningVersion="+env.InputStartingVersion
				}
			}
		}
		stage ('CreateHelpFolder'){
			steps{
				script{
				    echo "$params.SourceStreamName"
					basever="$params.SourceStreamName".substring(7,12)
					echo basever
					if(params.Version.length()>6) MajorMinor=params.Version.substring(0,5) else MajorMinor=params.Version
					bat "xcopy ${networkpath}\\Archer_Installations\\NuArcher_V${basever}\\HelpFiles\\*.* ${networkpath}\\Archer_Installations\\NuArcher_V${MajorMinor}\\HelpFiles\\ /f /s"
				}
			}
		}
	}
}

def exitwitherror(msg){
    input(message:msg ,ok:'Abort')
    throw new Exception(msg)
}
