definition.script:
pipeline {
    agent { node { label 'master' } }
    stages {
        stage('build') {
            steps {
                script {
                   def user_team =((String[])Jenkins.instance.securityRealm.loadUserByUsername(User.current().getId()).getAuthorities()).contains("DL-OMD-ENG-SCM") ? "DevOps": "Dev"
                   def userInput = input(
                    id: 'userInput', message: 'Hello'+User.current().getFullName()+"\nIdentified as "+user_team+ " user", parameters: [
                    [$class: 'ChoiceParameterDefinition', choices: 'Tasks\nFormal\nScheduled', description: 'Which build type you would like to run ?', name: 'BUILDTYPE'],
                    [$class: 'ChoiceParameterDefinition', choices: 'Debug & Release\nRelease\nDebug', description: 'In which modes ?', name: 'BUILDMODES'],
                    [$class: 'BooleanParameterDefinition', defaultValue: true, description: 'Build RDM?', name: 'RunRDM'],
                    [$class: 'ChoiceParameterDefinition', choices: 'Basic\nAdvanced\nDiagnostic\nCustom', description: 'Which build style would you like to run? \n\tBasic: Compilation only \n\tAdvanced: Compilation, Installers \n\tDiagnostic:Commpilation, Installers, SCA, Sanity, Nunit\n\tCustom: Run customized build steps ', name: 'BuildStyle'],
                    [$class: 'StringParameterDefinition', defaultValue:'', description: 'What should be your build version?', name: 'Build_Version'],
                    [$class: 'StringParameterDefinition', defaultValue:'', description: 'What is your build WorkSpace? \nExample: asaffori_Archer_17.12_Dev_Workspace', name: 'WorkSpaceName']])


                   def params = [
                    [$class: 'ChoiceParameterDefinition', choices: 'Tasks\nFormal\nScheduled', description: 'Would you like to generate installers for this build ?', name: 'BUILDTYPE'],
                    [$class: 'BooleanParameterDefinition', defaultValue: true, description: 'Would you like to run automatic Archer sanity tests for this build ?', name: 'RunATE'],
                    [$class: 'BooleanParameterDefinition', defaultValue: true, description: 'Would you like to generate installers for this build ?', name: 'BuidInstallers']]

                    userInput = input(
                    id: 'userInput', message: 'Hello Adham\n', parameters: params)

                }
            }
        }
    }
}
