 def getUserRTCWorkspaces(){
    wrap([$class: 'BuildUser']) {
        cmd="scm  list workspaces -r http://clm.kla-tencor.com/ccm -u omdbuild -P Scm07^& -c ${BUILD_USER_ID} -m 100"
        try{
            stdoutput = bat(returnStdout:true , script:cmd).trim()
        }catch(Exception e){
            throw e
        }
       // print ("stdout=" + stdoutput)
        def workspaces = []
        stdoutput.readLines().each {
            if (it.contains(BUILD_USER)){
                workspaces << it.substring(it.indexOf('\"')+1,it.lastIndexOf('\"'))
            }
        }

        return workspaces
    }
}
