definition.script:
pipeline {
    agent any
    parameters {
        string(name: 'Version', description: 'Which Version you want to release?')
        booleanParam(name: 'A600', defaultValue: false, description: 'Released For Tool Type')
        booleanParam(name: 'A700', defaultValue: false)
        booleanParam(name: 'ATL', defaultValue: false)
        string(name: 'Comments', description: 'What is your comments?')
		string(name: 'CompitableRDMVersion', description: 'Leave it with empty if no special RDM version')

    }
	stages {

	    stage ('validation') {
            steps ('vaiidate') {
	                script{
	                if(params.Version==""){
	                    msg="Version is not defined"
    				    input(message:msg ,ok:'Abort')
    					throw new Exception(msg)
	                }
	                if(params.A600==false&&params.A700==false&&params.ATL==false){
	                    msg="Released tool type is empty, at least you are releasing for one tool type"
    				    input(message:msg ,ok:'Abort')
    					throw new Exception(msg)
                    }
                }
            }
	    }
	    stage ('tooltype Process') {
	        steps ('tooltypes Process') {
	            script {
	                if(params.A600) {
	                   A600input=input (
	                        id:'A600input',
                             message:'About A600?',
                             ok:'Continue.',
                             parameters: 
                             [[$class:'BooleanParameterDefinition',name: 'A600SRN', defaultValue: true, description: 'Does it have SRN?'],
                             [$class:'BooleanParameterDefinition',name: 'A600Web', defaultValue: false, description: 'will it be publish on Web?'],
                             [$class: 'TextParameterDefinition',name: 'A600Customer',defaultValue: 'All', description: 'Which Customer will be released to?']]
	                    )
	                    env.A600SRN = A600input.A600SRN
	                    env.A600Web = A600input.A600Web
	                    env.A600Customer = A600input.A600Customer
	                }
	                if(params.A700) {
	                   A700input=input (
	                       id:'A700input',
                             message:'About A700?',
                             ok:'Continue.',
                             parameters: 
                              [[$class:'BooleanParameterDefinition',name: 'A700SRN', defaultValue: true, description: 'Does it have SRN?'],
                             [$class:'BooleanParameterDefinition',name: 'A700Web', defaultValue: false, description: 'will it be publish on Web?'],
                             [$class: 'TextParameterDefinition',name: 'A700Customer',defaultValue: 'All', description: 'Which Customer will be released to?']]
	                    )
	                    env.A700SRN = A700input.A700SRN
	                    env.A700Web = A700input.A700Web
	                    env.A700Customer = A700input.A700Customer
	                }
	                if(params.ATL) {
	                    ATLinput=input (
	                        id:'ATLinput',
                             message:'About ATL?',
                             ok:'Continue.',
                             parameters: 
                               [[$class:'BooleanParameterDefinition',name: 'ATLSRN', defaultValue: true, description: 'Does it have SRN?'],
                             [$class:'BooleanParameterDefinition',name: 'ATLWeb', defaultValue: false, description: 'will it be publish on Web?'],
                             [$class: 'TextParameterDefinition',name: 'ATLCustomer',defaultValue: 'All', description: 'Which Customer will be released to?']]
	                    )
	                    env.ATLSRN = ATLinput.ATLSRN
	                    env.ATLWeb = ATLinput.ATLWeb
	                    env.ATLCustomer = ATLinput.ATLCustomer
	                }

	            }
	        }

	    }
        stage ('Process Parameter') {
		    steps ('Parameter'){
		        script {
		            shortversion=params.Version.substring(0,8)
					majorminor=params.Version.substring(0,5)
					RDMVersion=params.CompitableRDMVersion==""?params.Version:params.CompitableRDMVersion
		            tooltype=(params.A600?" A600":"")+(params.A700?" A700":"")+(params.ATL?" ATL":"") 
		            toolSRN=(env.A600SRN&&params.A600?" A600":"")+(env.A700SRN&&params.A700?" A700":"")+(env.ATLSRN&&params.ATL?" ATL":"") 
		            publishweb=(params.A600?(env.A600Web?" A600: Will be published on Web.</br>":"A600: No Web publish.</br>"):"")+(params.A700?(env.A700Web?" A700: Will be published on Web.</br>":"A700: No Web publish.</br>"):"")+(params.ATL?(env.ATLWeb?" ATL: Will be published on Web.</br>":"ATL: No Web publish.</br>"):"")
					customer=(params.A600?"A600:"+env.A600Customer+"</br>":"")+(params.A700?"A700:"+env.A700Customer+"</br>":"")+(params.ATL?"ATL:"+env.ATLCustomer+"</br>":"") 
					mailgroup=(params.A600?"Agnes.Zhang@kla-tencor.com;":"")+(params.A700?"Adham.Saffori@kla-tencor.com;":"")

					string masterimage="</br>"
					if(params.A600)	masterimage+=new File("\\\\klasj\\ktfiles\\Regions\\Israel\\SWGRP\\OMDSWImages3\\A600.txt").text+"</br>"
					if(params.A700)	masterimage+=new File("\\\\klasj\\ktfiles\\Regions\\Israel\\SWGRP\\OMDSWImages3\\A700.txt").text+"</br>"
					if(params.ATL)	masterimage+=new File("\\\\klasj\\ktfiles\\Regions\\Israel\\SWGRP\\OMDSWImages3\\ATL.txt").text+"</br>"

					def buildnotice= "\\\\isr1appcc02\\Views\\OMD\\OMD-UN_"+majorminor+"\\UOMD_BLD_"+params.Version+"_code"+ "/OMD_Builds/JenkinsBuild/Instructions/InstallationInstruction.html";
	                def buildnoticefile = new File(buildnotice)
					if(buildnoticefile.exists()){
                		string phoenixversion="SW "+shortversion+" requires upgrade of the Handler SW to Phoenix SWV V"
                		echo "found the buildnotice file"				
                		builtnoticecontent=buildnoticefile.readLines()
                		for(String line : builtnoticecontent){
                			if(line.contains(">Phoenix<")){
                			 string subline=line.substring(line.indexOf("<TD>")+4,line.length()-5)
                			 phoenixversion+=subline

                			continue
                			}
                		  }
                	}

		            def thirdpartyism= "\\\\isr1appcc02\\Views\\OMD\\OMD-UN_"+majorminor+"\\UOMD_BLD_"+params.Version+"_code"+ "/Installation/Archer_Family/Installshield/Archer_SW_Installation/uArcherThirdPartyInstallation/ThirdPartySetup.ism";
	                def thirdparty
	                thirdparty= new File(thirdpartyism)

                	if(thirdparty.exists()){
                		string thirdpartyversion="<table>"
                		echo "found the third party ism file"				
                		thirdpartycontent=thirdparty.readLines()
                		for(String line : thirdpartycontent){
                			if(line.contains("PATH_TO_ARCHER_INSTALLATION&gt;\\SupportFiles")||line.contains("PATH_TO_ARCHER_INSTALLATION&gt;\\&lt;ARCHER_VER2&gt;\\SupportFiles")){
                			if(line.contains("Disk"))continue
                			 string subline=line.substring(line.indexOf("SupportFiles\\")+13,line.length()-16).replaceAll("\\\\","<//TD><TD>")
                			 thirdpartyversion+="<TR><TD>"+subline+"</TD></TR>"
                            echo subline
                			continue
                			}
                		  }
                		 string thirdpartyversion+="<//table>"
                	}

		            echo thirdpartyversion
		            mailcontent="Archer SWV  ${shortversion}(${params.Version}) is released for ${tooltype}</br>"+
                    "${params.Comments}</br>"+
                    "<strong><u>Tool SW</u></strong></br>"+
                    "SWV ${params.Version} Installation kit is available in \\\\klasj\\ktfiles\\Regions\\Israel\\Eng\\Download\\uArcher_V${shortversion}\\Tool\\ArcherSWInsKIT_${params.Version}</br>"+
					"Installation instructions are residing in Documentation folder</br>"+
                    "${toolSRN} SRN is residing in Documentation folder.</br>"+
					"</br>"+
					"<strong><u>Compatible RDM SW</u></strong></br></br>"+
					"RDM ${shortversion} is available at \\\\klasj\\ktfiles\\Regions\\Israel\\Eng\\Download\\uArcher_V${shortversion}\\RDM\\RDM_${RDMVersion}</br>"+
					"Installation instructions available in residing in Documentation folder.</br>"+
					"</br>"+
					"<strong><u>Tool Config Utility</u></strong></br>"+
					"Tool configuration utility can be found at  \\\\klasj\\ktfiles\\Regions\\Israel\\Eng\\Download\\uArcher_V${shortversion}\\Tool\\ArcherSWInsKIT_${params.Version}\\ToolConfiguration </br>"+
					"</br>"+
					"<strong><u>Phoenix</u></strong></br>"+
					"${phoenixversion}</br>"+
					"Phoenix installation can be found in \\\\klasj\\ktfiles\\Regions\\Israel\\Eng\\Download\\uArcher_V${shortversion}\\Tool\\ArcherSWInsKIT_${params.Version}\\Phoenix</br>"+
					"</br>"+
					"<strong><u>Latest master images are:</u></strong></br>"+
					"${masterimage}"+
					"</br></br>"+
					"<strong><u>Publish on Web:</u></strong></br>"+
					"${publishweb}"+
					"</br>"+
					"<strong><u>Customer:</u></strong></br>"+
					"${customer}"+
					"</br>"+
					"<strong><u>Third Party Version:</u></strong></br>"+
					"${thirdpartyversion}"

		        }
		        emailext (to: "Agnes.Zhang@KLA-tencor.com",
                subject: "Archer SWV ${shortversion} (${params.Version}) is released for ${tooltype} --- for your reivew only",
                body: "${mailcontent}");
		    }
        }

	    stage ('Send review') {
	        steps {
                echo "Draft has been sent out for review"
            }
		    input {
                message "Is the mail content fine?"
                ok "Yes, we can release it"
            }

        }
        stage ('Send Mail') {
            steps ('SendMail') {
		        emailext (to: "${mailgroup}",
                subject: "Archer SWV ${shortversion} (${params.Version}) is released for ${tooltype}",
                body: "${mailcontent}");
		    }
        }
	}
}
