definition.script:
import groovy.io.FileType

pipeline {
    agent any
    parameters {
        string(name: 'Version', description: 'Which Version you want to release?')
        booleanParam(name: 'A600', defaultValue: false, description: 'Released For Tool Type')
        booleanParam(name: 'A700', defaultValue: false)
        booleanParam(name: 'ATL', defaultValue: false)
        text(name: 'Comments', description: 'What is your comments?')
		booleanParam(name: 'A600Web', defaultValue: false, description: 'Which Version will be publish on Web?')
        booleanParam(name: 'A700Web', defaultValue: false)
        booleanParam(name: 'ATLWeb', defaultValue: false)
        string(name: 'A600Customer')
        string(name: 'A700Customer')
        string(name: 'ATLCustomer') 
        text(name: 'Comments on Phoenix installation:', description: 'Input your comments regarding the Phoenix installation:')

    }
	stages {

	    stage ('validation') {
            steps ('vaiidate') {
	                script{
	                if(params.Version==""){
    				    exitwitherror("Version is not defined")
	                }
	                if(params.A600==false&&params.A700==false&&params.ATL==false){
    				    exitwitherror("Released tool type is empty, at least you are releasing for one tool type")
                    }
                }
            }
	    }

        stage ('Process Parameter') {
		    steps ('Parameter'){
		        script {
		            shortversion=params.Version.substring(6,8)=='00'?Version.substring(0,6):params.Version.substring(0,8)
					majorminor=params.Version.substring(0,5)

					//get compitable RDM version
					def RDMVersion
					File RDMFolder =new File("\\\\klasj\\ktfiles\\Regions\\Israel\\Eng\\Download\\uArcher_V"+shortversion+"\\RDM")
					if(!RDMFolder.exists())exitwitherror("RDM kit folder doesn't exist")
					File[] RDMFolderContent=RDMFolder.listFiles()
					RDMFolderContent.each{
					    RDMVersion=it.getName().substring(4,18)
					}
					if(RDMVersion=="")exitwitherror("RDM Version is not defined")
                    echo RDMVersion

		            tooltype=(params.A600?" A600":"")+(params.A700?" A700":"")+(params.ATL?" ATL":"") 

		            def toolSRN=""
                    File DocFolder =new File("\\\\klasj\\ktfiles\\Regions\\Israel\\Eng\\Download\\uArcher_V"+shortversion+"\\Tool\\ArcherSWInsKIT_"+params.Version+"\\Documentation")
					if(!DocFolder.exists())exitwitherror("Archer kit document folder doesn't exist")
					File[] DocContent=DocFolder.listFiles()
					DocContent.each{
					    if(it.getName().contains('SRN'))toolSRN=toolSRN+it.getName()+" "
					}
					if(toolSRN==""){
					    toolSRN=="</br>"
					}
					else{
					    toolSRN+=" is residing in Documentation folder.</br>"
					}
                    echo toolSRN

					if(params.Comments==null)params.Comments="</br>"

		            A600webtext=(params.A600Web?" A600: Will be published on Web.</br>":"A600: No Web publish.</br>")
		            A700webtext=(params.A700Web?" A700: Will be published on Web.</br>":"A700: No Web publish.</br>")
		            ATLwebtext=(params.ATLWeb?" ATL: Will be published on Web.</br>":"ATL: No Web publish.</br>")

		            publishweb=(params.A600?A600webtext:"")+(params.A700?A700webtext:"")+(params.ATL?ATLwebtext:"") 
		            echo publishweb

					customer=(params.A600?"A600:"+params.A600Customer+"</br>":"")+(params.A700?"A700:"+params.A700Customer+"</br>":"")+(params.ATL?"ATL:"+params.ATLCustomer+"</br>":"") 
					echo customer

					mailgroup=(params.A600?"Agnes.Zhang@kla-tencor.com;":"")+(params.A700?"Adham.Saffori@kla-tencor.com;":"")
					echo mailgroup

					string masterimage=""
					File masterimagefile=new File("\\\\isr1appcc02\\Views\\OMD\\OMD-UN_"+majorminor+"\\UOMD_BLD_"+params.Version+"_code"+ "/OMD_Builds/JenkinsBuild/Instructions/MasterImage.html")
	                if(!masterimagefile.exists())exitwitherror("Master image html doesn't exist under code view")
	                masterimagecontent=masterimagefile.text.split("<tr>")
	                for(String line : masterimagecontent){
	                   echo line
	                   // if(params.A600)	masterimage+=echo masterimage
					//if(params.A700)	masterimage+=masterimagecontent.substring(masterimagecontent.indexOf("A700")+9,masterimagecontent.indexOf("td>",masterimagecontent.indexOf("A700")))
					//echo masterimage
					//if(params.ATL)	masterimage+=masterimagecontent.substring(masterimagecontent.indexOf("ATL")+9,masterimagecontent.indexOf("td>",masterimagecontent.indexOf("ATL")))

	                }

					def buildnotice= "\\\\isr1appcc02\\Views\\OMD\\OMD-UN_"+majorminor+"\\UOMD_BLD_"+params.Version+"_code"+ "/OMD_Builds/JenkinsBuild/Instructions/InstallationInstruction.html";
	                def buildnoticefile = new File(buildnotice)
					if(buildnoticefile.exists()){
                		string phoenixversion="SW "+shortversion+" requires upgrade of the Handler SW to Phoenix SWV V"
                		echo "found the buildnotice file"				
                		builtnoticecontent=buildnoticefile.readLines()
                		for(String line : builtnoticecontent){
                			if(line.contains(">Phoenix<")){
                			 string subline=line.substring(line.indexOf("<TD>")+4,line.length()-5)
                			 phoenixversion+=subline

                			continue
                			}
                		  }
                	}
                	else{
                	    exitwitherror("Build notice file under code view folder doesn't exist")
                	}

		            def thirdpartyism= "\\\\isr1appcc02\\Views\\OMD\\OMD-UN_"+majorminor+"\\UOMD_BLD_"+params.Version+"_code"+ "/Installation/Archer_Family/Installshield/Archer_SW_Installation/uArcherThirdPartyInstallation/ThirdPartySetup.ism";
	                def thirdparty=new File(thirdpartyism)
					def KTCS="",KTCSHF="",Nexus="",NexusHF="",KTGem="",KTGemHF="",PSGFA="",PSGFAHF=""

                	if(thirdparty.exists()){
                		echo "found the third party ism file"				
                		thirdpartycontent=thirdparty.readLines()

                		for(String line : thirdpartycontent){
                			if(line.contains("PATH_TO_ARCHER_INSTALLATION&gt;\\SupportFiles")||line.contains("PATH_TO_ARCHER_INSTALLATION&gt;\\&lt;ARCHER_VER2&gt;\\SupportFiles")){
                    			if(line.contains("IMFA")||line.contains("FDC_FA_KTGEM"))continue
                    			if(line.contains("NeXus")){
                    			    if(line.contains("ktgem")){
                    			        if(line.contains("HF")){
                        			        KTGemHF+=line.substring(line.indexOf("SupportFiles\\NeXus\\ktgem\\")+25,line.length()-26)+" "
                        			        continue
                    			        }
                    			        continue
                    			    }
                    			    else{
                    			        if(line.contains("HF")){
                    			            NexusHF+=line.substring(line.indexOf("SupportFiles\\NeXus\\")+19,line.length()-26)+" "
                    			            continue
                    			        }
                    			        Nexus=line.substring(line.indexOf("SupportFiles\\NeXus\\")+19,line.length()-26)   
                    			    }
                    			}
                    			if(line.contains("KTCS")){
                    			    if(line.contains("HF")){
                    			        KTCSHF+=line.substring(line.indexOf("SupportFiles\\KTCS\\")+18,line.length()-26)+" "
                    			        continue
                    			    }
                    			    KTCS=line.substring(line.indexOf("SupportFiles\\KTCS\\")+18,line.length()-26)
                    			}
                    			if(line.contains("PSG-FA")){
                    			    if(line.contains("HF")){
                    			        PSGFAHF+=line.substring(line.indexOf("SupportFiles\\PSG-FA\\")+20,line.length()-26)+" "
                    			        continue
                    			    }
                    			    PSGFA=line.substring(line.indexOf("SupportFiles\\PSG-FA\\")+20,line.length()-26)
                    			}
                			}
                		  }
                		 string thirdpartyversion="<table>"+
                		 "<TR><TD>KTCS<//TD><TD>"+KTCS+" "+KTCSHF+"<//TD><//TR>"+
                		 "<TR><TD>Nexus<//TD><TD>"+Nexus+" "+NexusHF+"<//TD><//TR>"+
                		 "<TR><TD>KTGem<//TD><TD>"+KTGem+" "+KTGemHF+"<//TD><//TR>"+
                		 "<TR><TD>PSG-FA<//TD><TD>"+PSGFA+" "+PSGFAHF+"<//TD><//TR><//table>"
                	}
                	else{
                	    exitwitherror("Thirdparty installshield file under code view folder doesn't exist")
                	}

		            echo thirdpartyversion
		            mailcontent="Archer SWV  ${shortversion}(${params.Version}) is released for ${tooltype}</br>"+
                    "${params.Comments}</br>"+
                    "<strong><u>Tool SW</u></strong></br>"+
                    "SWV ${params.Version} Installation kit is available in \\\\klasj\\ktfiles\\Regions\\Israel\\Eng\\Download\\uArcher_V${shortversion}\\Tool\\ArcherSWInsKIT_${params.Version}</br>"+
					"Installation instructions are residing in Documentation folder</br>"+
                    "${toolSRN}"+
					"</br>"+
					"<strong><u>Compatible RDM SW</u></strong></br>"+
					"RDM ${shortversion} is available at \\\\klasj\\ktfiles\\Regions\\Israel\\Eng\\Download\\uArcher_V${shortversion}\\RDM\\RDM_${RDMVersion}</br>"+
					"Installation instructions available in residing in Documentation folder.</br>"+
					"</br>"+
					"<strong><u>Tool Config Utility</u></strong></br>"+
					"Tool configuration utility can be found at  \\\\klasj\\ktfiles\\Regions\\Israel\\Eng\\Download\\uArcher_V${shortversion}\\Tool\\ArcherSWInsKIT_${params.Version}\\ToolConfiguration </br>"+
					"</br>"+
					"<strong><u>Phoenix</u></strong></br>"+
					"${phoenixversion}</br>"+
					"Phoenix installation can be found in \\\\klasj\\ktfiles\\Regions\\Israel\\Eng\\Download\\uArcher_V${shortversion}\\Tool\\ArcherSWInsKIT_${params.Version}\\Phoenix</br>"+
					"</br>"+
					"<strong><u>Latest master images are:</u></strong></br>"+
					"${masterimage}"+
					"</br></br>"+
					"<strong><u>Publish on Web:</u></strong></br>"+
					"${publishweb}"+
					"</br>"+
					"<strong><u>Customer:</u></strong></br>"+
					"${customer}"+
					"</br>"+
					"<strong><u>Third Party Version:</u></strong></br>"+
					"${thirdpartyversion}"

		        }
		        emailext (to: "Agnes.Zhang@KLA-tencor.com",
                subject: "Archer SWV ${shortversion} (${params.Version}) is released for ${tooltype} --- for your reivew only",
                body: "${mailcontent}");
		    }
        }

	    stage ('Send review') {
	        steps {
                echo "Draft has been sent out for review"
                echo "${mailcontent}"
            }
		    input {
                message "Is the mail content fine?"
                ok "Yes, we can release it"
            }

        }

        stage ('Exculde files'){
             steps {
				script{
					excludetext="\n#Added by Auto-Release script\n"+"UOMD_"+Version+".*\n"+"Archer_Installation_"+Version+".*\n"+"RDM_"+Version+".*\n"+"TDD_"+Version+".*\n"
					new File("\\\\isr1appcc02\\SCM\\OMD\\Jenkins_CleanupConfigurations\\AgnesTest.txt").append(excludetext)
				}


            }
        }
        stage ('Send Mail') {
            steps ('SendMail') {
		        emailext (to: "${mailgroup}",
                subject: "Archer SWV ${shortversion} (${params.Version}) is released for ${tooltype}",
                body: "${mailcontent}");
		    }
        }
	}
}

def exitwitherror(msg){
    input(message:msg ,ok:'Abort')
    throw new Exception(msg)
}
