def getRegistrykey(host){
    stage('get Registry key'){
        node(host){
            def commit = bat (returnStdout: true, script: '''reg query "HKEY_LOCAL_MACHINE\\SOFTWARE\\KLA-Tencor\\OM\\5300\\Machine\\Software Version\\Platform Software Version" /ve''').split()
            return commit.reverse()[0] //Getting the last element of the stdout
        }
    }
   }

def sendTestPlan(controller, sut, version){
    testsuite = params.TestSuite.replace('.csv', env.BUILD_NUMBER+'.csv');
    try{
        return sendHttpRequest('send test plan','savecsv',[controller,params.ToolType.replaceAll("\\s",""),sut,version,env.SimulatorEngine,testsuite,convertPathToURI(currentBuild.getAbsoluteUrl())])
    }catch(Exception e){
        print ("Exception error:"+e.getMessage())
        error ("message:"+e.getMessage())
    }
}

def saveCsvDataToDatabase(testId,csvFilePath,productPath){
    try{
        return sendHttpRequest('save csv data to database','savecsvdata',[testId,csvFilePath,productPath])
    }catch(Exception e){
        print ("Exception error:"+e.getMessage())
        error ("message:"+e.getMessage())
    }
}

def saveReportByPlanId(testId){
    return sendHttpRequest('save Report By Plan Id','savereportbypath',[testId])
}

def updateStatus(testId, value){
    return sendHttpRequest('update value','updatevalue',[testId,value])
}

def getSuiteFullPath(productPath){
    println ("Working with productPath:"+productPath)
    println ("Working with params.TestSuite:"+params.TestSuite)
    File file;
    def updatedSearchPath= convertLocalPathToShare(suitesRootFolderPerToolType[productPath],env.ATEControllerEngine)
    print('Looking for suite: '+params.TestSuite+ 'in path: '+updatedSearchPath);
    def suitScriptPath=getPath(params.TestSuite,updatedSearchPath)
    if (suitScriptPath==null){
        error ('Unable to find suite at\''+updatedSearchPath+'\'')
    }else {
        print ('Suite found:'+suitScriptPath)
        file=new File (suitScriptPath)
        print ('file name:'+file.name)
        print ('file path:'+file.getAbsoluteFile().getParent())
        print ('file path:'+file.getPath())
        print ('file path:'+file.getCanonicalPath())

    }
    def suitScriptPath_fixed = convertSharePathToLocal(file.getAbsoluteFile().getParent());
    return suitScriptPath_fixed
}


