def sendHttpRequest(stepDesc,action,params){
    stage(stepDesc){
        //node('master'){
            try{
                params.add(0, action);
                print ('Running HTTP request for: httpRequest http://'+ReportingSystemURL + '/report/' + params.join('/'))
                def response = httpRequest 'http://'+ReportingSystemURL + '/report/' + params.join('/');
                println('response: '+ response.content);
                return response.content;
            }catch(Exception e){
                error (message:e.getMessage())
            }
        //}
    }
}
def runAutoDeploySanity(testId, path, scriptNumber,sut, controller,version){
    def testsuite = params.TestSuite.replace('.csv', '');
    def ateBasePath = '\\\\'+env.ATEControllerEngine+'\\c$\\ATE_Automation\\'+ ToolTypeShortName[params.ToolType];
    def MajorMinor_Ver2 = params.MajorMinor_Ver.replace(',', '');
    println(MajorMinor_Ver2)
    def BaseImage2 = params.BaseImage.replace(',', '');
    println('BaseImage2: '+BaseImage2)


        //node('master'){
            stage('run Auto Deploy Sanity'){
              def result
              result = build job: 'ATARS_Auto Deploy Sanity',propagate:false,
                    parameters: [
                                 [$class: 'StringParameterValue', name: 'ATEControllerEngine', value: controller], //we have it
                                 [$class: 'StringParameterValue', name: 'ToolType', value: params.ToolType ], //we have it
                                 [$class: 'StringParameterValue', name: 'TestSuite', value: testsuite+env.BUILD_NUMBER ],
                                 [$class: 'StringParameterValue', name: 'BUILD_VERSION', value: version ], 
                                 [$class: 'StringParameterValue', name: 'SimulatorEngine', value: sut  ],
                                 [$class: 'StringParameterValue', name: 'TEST_ID', value: testId ],
                                 [$class: 'StringParameterValue', name: 'SCRIPT_NUMBER', value: scriptNumber ],
                                 [$class: 'StringParameterValue', name: 'TestSuitePath', value: path ],
                                 [$class: 'StringParameterValue', name: 'MajorMinor_Ver', value: MajorMinor_Ver2 ],//we have it
                                 [$class: 'StringParameterValue', name: 'BaseImage', value: BaseImage2 ],//we have it
                                 [$class: 'StringParameterValue', name: 'BaseImagePath', value: params.BaseImagePath ],//we have it
                                 [$class: 'StringParameterValue', name: 'BUILDTYPE', value: params.BUILDTYPE ],//we have it
                                 [$class: 'BooleanParameterValue', name: 'RestoreAndReinstall', value: params.RestoreAndReinstall ],//we have it   
                                 [$class: 'StringParameterValue', name: 'ATEBasePath', value: ateBasePath ],
                                [$class: 'StringParameterValue', name: 'product', value: ToolTypeShortName[params.ToolType] ],
                                [$class: 'BooleanParameterValue', name: 'RunNunitTests', value: params.RunNunitTests ]
                                 ]

            currentBuild.result = result.result
        //}
    }
}
def getEmailData(testId){
    return sendHttpRequest('get email data','getemaildata',[testId])
}
def sendEmail(testId,emailInfo){
        stage('send email'){
        //node('master'){
            wrap([$class: 'BuildUser']) {
                emailext subject: "${env.JOB_NAME} - Build # ${env.BUILD_NUMBER} - ${currentBuild.result}",
                    body: ReportingSystemURL + '/reportsDetails/planId/' + testId +   '${SCRIPT, template="klaemail.groovy"}' + emailInfo,
                    recipientProviders: [
                        [$class: 'RequesterRecipientProvider']
                    ], 
                    replyTo: '$DEFAULT_REPLYTO',
                    to: "${env.BUILD_USER}"
                }
        //}
    }
}



def getFreeHostPerLabel(label) {
    selectedNode='-1'
    Jenkins.getInstance().getLabel(label).getNodes().any { node ->
            if (!node.getComputer().isOffline() && node.getComputer().countBusy()==0){
                println "Selected Node: " +  node.getNodeName()
                selectedNode = node.getNodeName()
                return selectedNode
            }
    }
    return selectedNode
}

def copyCsvFileName(path){
    node(env.ATEControllerEngine){
        bat (script:""" copy ${path}\\${params.TestSuite} ${path}\\${params.TestSuite.replace('.csv', env.BUILD_NUMBER+'.csv')} || exit /b """)
    }
}
def deleteCsvFileName(path){
    node(env.ATEControllerEngine){
        bat ( script:""" del /s /q ${path}\\*${env.BUILD_NUMBER}.csv || exit /b """)
    }
}

def getBuildAbortionUser(){
    def causee = ''
    def actions = currentBuild.getRawBuild().getActions(jenkins.model.InterruptedBuildAction)
    println actions;
    for (action in actions) {
        def causes = action.getCauses()
        // on cancellation, report who cancelled the build
        for (cause in causes) {
            causee = cause.getUser().getDisplayName()
            println 'here';
            println causee;
            isAborted = true;
            cause = null
        }
        causes = null
        action = null
    }
    actions = null
}

def getPath(String drl, String whereIAm) {
    //node ('master') {
        File dir = new File(whereIAm); //StaticMethods.currentPath() + "\\src\\main\\resources\\" + 
        for(File e : dir.listFiles()) {
            if(e.isFile() && e.getName().equals(drl)) {return e.getPath();}
            if(e.isDirectory()) {
                String idiot = getPath(drl, e.getPath());
                if(idiot != null) {return idiot;}
            }
        }
        return null;
    //}
}
def convertLocalPathToShare(String path,String machine){
    path = path.replace(':','$')
    path= '\\\\'+env.ATEControllerEngine+'\\'+path
    return path
}

def convertSharePathToLocal(String path){
    path = path.replace('$',':')
    path = path.substring(path.indexOf(':')-1)
    return path
}

def convertPathToURI(String path){
    path = path.replace('\\','%5C')
    path = path.replace('/','%5C')
    return path
}




def testId = '';

def tempHost = '';
def sutIp = '';
def host = '';
def waitA = false;
def repeatee = 0;
def path = '';
def scriptNumber = '';
def version = '';
def isAborted = false;
def isUNSTABLE = false;
node('master'){
 wrap([$class: 'TimestamperBuildWrapper']) {   
    try{
        controller = env.ATEControllerEngine
        println 'env.SimulatorEngine: ' + env.SimulatorEngine
        currentBuild.description = "Executing suite '"+params.TestSuite+"' with "+params.ToolType
        lock('looking for free controller machine'){
            if (env.ATEControllerEngine == 'Controller'){
                sleep 3 //sleep for 3 seconds to get the previous selected node be alocatted
                println 'choose Controller parameter value will choose from list of avaliable Controllers'
                waitUntil {
                    println 'looking for avialable controller'
                    controller = getFreeHostPerLabel('Controller');
                    waitA = controller != '-1' ? true : false;
                    repeatee++;
                    if(!waitA){
                        println 'sleep 60 sec'
                        sleep 60
                    }
                    return waitA;
                }
                println 'controller '+controller
                env.ATEControllerEngine = controller;
            }else{
                println 'choose controller by name'
                controller = env.ATEControllerEngine;
            }
        }
       // lock(resource: controller/**, inversePrecedence: true**/){
            lock('looking for free simulator'){
                if (env.SimulatorEngine == 'sut'){
                    sleep 3 //sleep for 3 seconds to get the previous selected node be alocatted
                    println 'choose sut parameter will choose from list of avaliable simulators'
                    waitUntil {
                        println 'looking for avialable sut'
                        host = getFreeHostPerLabel('sut');
                        waitA = host != '-1' ? true : false;
                        repeatee++;
                        if(!waitA){
                            println 'sleep 60 sec'
                            sleep 60
                        }
                        return waitA;
                    }
                    println 'host '+host
                    env.SimulatorEngine = host;
                }else{
                    println 'choose simulator by name'
                    host = env.SimulatorEngine;
                }
                version = params.RestoreAndReinstall ? env.BUILD_VERSION:getRegistrykey(host).substring(1).trim();
            }

            lock(resource: host/**, inversePrecedence: true**/){
                stage('lock slave'){
                    node(host){
                        def productPath ='';
                        productPath = ToolTypeShortName[params.ToolType];
                        println 'productPath ' + productPath;
                        //1: save details
                        testId = sendTestPlan(env.ATEControllerEngine, host, version);
                        println 'testid:'+testId;
                        //2:get the master suite real path 
                        path = getSuiteFullPath(productPath);
                        println 'path: ' + path;
                        copyCsvFileName(path)
                        //3:save data to database
                        def suiteRemoteLocation = convertLocalPathToShare(path,env.ATEControllerEngine)
                        def suiteFullPath=suiteRemoteLocation + '\\' + params.TestSuite
                        pathForJS=convertPathToURI(suiteFullPath)
                        saveCsvDataToDatabase(pathForJS,testId,productPath);
                        scriptNumber = '';
                        //4:save the script number that the eggplanet should execute
                        scriptNumber = updateScriptsNumber(testId);
                        println 'scriptNumber: ' + scriptNumber;
                        updateStatus(testId, 'In_Progress')
                        //6:execute the KLA logic 
                        currentBuild.description = "Executing suite '"+params.TestSuite+"' with "+params.ToolType + " on " + host
                        if(params.RestoreAndReinstall){
                            println 'full suite' 
                            runAutoDeploySanity(testId,suiteRemoteLocation, scriptNumber, host, controller, version);
                        }else{
                            println 'regualr suite' 
                            runAutoDeploySanity(testId,suiteRemoteLocation, scriptNumber, host, controller,version);
                        }
                    }
                }
            }
        //}   
        print ("currentBuild.result="+currentBuild.result)
        }catch(Exception e){
            println 'Execution end with exception: ' + e;
            currentBuild.result='ABORTED'
        }finally  {
            if (path != '')
                deleteCsvFileName(path)
            if(currentBuild.result == 'SUCCESS'){
                updateStatus(testId, 'Finish');
                saveReportByPlanId(testId);
            } else if(currentBuild.result == 'UNSTABLE'){
                updateStatus(testId, 'Finish');
                saveReportByPlanId(testId);
            }else if(currentBuild.result == 'FAILURE'){
                updateStatus(testId, 'Crash');
            }else if(currentBuild.result == 'ABORTED'){
                updateStatus(testId, 'Aborted');
            }
            def emailInfo = getEmailData(testId);
            sendEmail(testId, emailInfo);
        }
 }
}
