import static groovy.io.FileType.FILES

def getProductPath(val){
    List<String> artifacts = new ArrayList<String>()
    def ToolTypesAndFolders=['Archer700':'////'+ATEControllerEngine+'//c$//ATE_Automation//A700//ATE//TestCases//SQC//Imaging_New_TCUI',
    'Archer600':'////'+ATEControllerEngine+'//c$//ATE_Automation//A600//ATE//TestCases//SQC//Imaging',
    'Archer TL':'////'+ATEControllerEngine+'//c$//ATE_Automation//ATL//ATE//TestCases//SQC//MBS//New_TCUI',
    'A500':'////'+ATEControllerEngine+'//c$//ATE_Automation//A500//ATE//TestCases//SQC']

    new File(ToolTypesAndFolders[val]).eachFileRecurse(FILES) {
        if(it.name.toLowerCase().contains('suite')) {
            artifacts.add(it.name)
        }
    }         
        return artifacts;
}
